mapper.update:
update BOARD set 
LIKEHIT = LIKEHIT+1
where BNO = #{bno}

update BOARD set 
LIKEHIT = LIKEHIT - 1
where BNO = #{bno}

mapper.select:
    	SELECT *
			FROM ( 
				SELECT a.*, ROWNUM AS rnum
			    FROM ( SELECT * FROM board ORDER BY bno DESC) a
				)
			WHERE rnum BETWEEN #{start} AND #{end}
