UPDATE board SET bre_step = bre_step + 1
WHERE bref = #{bref} AND bre_step > #{bre_step };				
INSERT INTO board VALUES (SEQ_BOARD_BNO.NEXTVAL, #{bref }, #{btitle}, #{bcontent}, #{mid}, #{breStep } + 1, #{breLevel} + 1, #{fileName, jdbcType=VARCHAR }, #{filePath, jdbcType=VARCHAR });
